import React from 'react';
import { useGameState } from '../../hooks/useGameState';
import Button from '../ui/Button';
import { levels } from '../../data/levels';

const DecryptionInput = () => {
  const { 
    currentLevel, 
    userAnswer, 
    setUserAnswer, 
    checkAnswer, 
    showSolution 
  } = useGameState();

  const level = levels[currentLevel];

  const handleInputChange = (index, value) => {
    const newAnswer = userAnswer.split('');
    newAnswer[index] = value.toUpperCase();
    setUserAnswer(newAnswer.join(''));
  };

  return (
    <div className="mt-8">
      <h3 className="text-center font-semibold mb-4">Enter Decrypted Message</h3>
      <div className="flex justify-center gap-2 mb-4">
        {level.correctMessage.split('').map((_, idx) => (
          <input
            key={idx}
            type="text"
            maxLength="1"
            className="message-box bg-white"
            value={userAnswer[idx] || ''}
            onChange={(e) => handleInputChange(idx, e.target.value)}
            disabled={!showSolution}
            aria-label={`Letter position ${idx + 1}`}
          />
        ))}
      </div>

      <div className="flex justify-center">
        <Button
          variant="success"
          onClick={checkAnswer}
          disabled={!showSolution || userAnswer.length !== level.correctMessage.length}
        >
          Check Answer
        </Button>
      </div>
    </div>
  );
};

export default DecryptionInput;
