import React from 'react';
import { useGameState } from '../../hooks/useGameState';
import { levels } from '../../data/levels';

const MessageDisplay = () => {
  const { currentLevel, showSolution, decryptionKey } = useGameState();
  const level = levels[currentLevel];

  return (
    <div className="mb-8">
      <div className="text-center mb-4">
        <h2 className="text-xl font-semibold mb-2">Encrypted Message</h2>
        <div className="flex justify-center gap-2">
          {level.encryptedMessage.split('').map((char, idx) => (
            <div key={idx} className="message-box bg-white">
              {char}
            </div>
          ))}
        </div>
      </div>

      {showSolution && (
        <div className="mt-4 p-4 bg-green-100 rounded-lg">
          <p className="text-center text-green-800 font-medium">
            Decryption Key: {decryptionKey}
          </p>
        </div>
      )}

      {level.hint && (
        <div className="mt-4 text-center text-gray-600 italic">
          Hint: {level.hint}
        </div>
      )}
    </div>
  );
};

export default MessageDisplay;
