import React, { createContext, useReducer, useMemo } from 'react';
import { levels } from '../data/levels';

export const GameContext = createContext();

const initialState = {
  currentLevel: 0,
  selectedDots: new Set(),
  encryptedMessage: levels[0].encryptedMessage,
  decryptionKey: '',
  isRunning: false,
  showSolution: false,
  userAnswer: '',
  score: 0,
  timeSpent: 0,
  errors: 0,
  gameComplete: false
};

const gameReducer = (state, action) => {
  switch (action.type) {
    case 'TOGGLE_DOT':
      const newDots = new Set(state.selectedDots);
      const dotId = `${action.row}-${action.col}`;
      if (newDots.has(dotId)) {
        newDots.delete(dotId);
      } else {
        newDots.add(dotId);
      }
      return { ...state, selectedDots: newDots };

    // ... rest of the reducer cases ...
    default:
      return state;
  }
};

export const GameProvider = ({ children }) => {
  const [state, dispatch] = useReducer(gameReducer, initialState);

  const value = useMemo(() => ({
    state,
    dispatch
  }), [state]);

  return (
    <GameContext.Provider value={value}>
      {children}
    </GameContext.Provider>
  );
};
