import React from 'react';
import { useGameState } from '../../hooks/useGameState';
import { gameConfig } from '../../constants/gameConfig';
import Button from '../ui/Button';

const BombeBoard = () => {
  const { selectedDots, toggleDot, runBombe, isRunning } = useGameState();
  const { rows, cols } = gameConfig.gridSize;

  return (
    <div className="bg-gray-100 p-6 rounded-lg shadow-md">
      <div className="grid gap-4" 
        style={{ 
          gridTemplateColumns: `repeat(${cols}, minmax(0, 1fr))`,
          gridTemplateRows: `repeat(${rows}, minmax(0, 1fr))`
        }}>
        {Array.from({ length: rows * cols }, (_, i) => {
          const row = Math.floor(i / cols);
          const col = i % cols;
          const dotId = `${row}-${col}`;

          return (
            <button
              key={dotId}
              className={`bombe-dot ${
                selectedDots.has(dotId) ? 'bombe-dot-selected' : 'bg-white'
              }`}
              onClick={() => toggleDot(row, col)}
              disabled={isRunning}
              aria-label={`Dot at position ${row},${col}`}
            />
          );
        })}
      </div>

      <div className="mt-6 flex justify-center">
        <Button
          variant="primary"
          size="large"
          onClick={runBombe}
          disabled={isRunning || selectedDots.size === 0}
        >
          {isRunning ? 'Running Bombe...' : 'Run Bombe'}
        </Button>
      </div>
    </div>
  );
};

export default BombeBoard;
