import React, { createContext, useReducer, useContext } from 'react';
import { levels } from '../data/levels';

export const GameContext = createContext();

const initialState = {
  currentLevel: 0,
  selectedDots: new Set(),
  encryptedMessage: levels[0].encryptedMessage,
  decryptionKey: '',
  isRunning: false,
  showSolution: false,
  userAnswer: '',
  score: 0,
  timeSpent: 0,
  errors: 0,
  gameComplete: false
};

const gameReducer = (state, action) => {
  switch (action.type) {
    case 'TOGGLE_DOT':
      const newDots = new Set(state.selectedDots);
      const dotId = `${action.row}-${action.col}`;
      if (newDots.has(dotId)) {
        newDots.delete(dotId);
      } else {
        newDots.add(dotId);
      }
      return { ...state, selectedDots: newDots };

    case 'START_BOMBE':
      return { 
        ...state, 
        isRunning: true,
        showSolution: false,
        decryptionKey: ''
      };

    case 'FINISH_BOMBE':
      return {
        ...state,
        isRunning: false,
        showSolution: true,
        decryptionKey: action.key
      };

    case 'SET_USER_ANSWER':
      return {
        ...state,
        userAnswer: action.answer
      };

    default:
      return state;
  }
};

export const GameProvider = ({ children }) => {
  const [state, dispatch] = useReducer(gameReducer, initialState);

  return (
    <GameContext.Provider value={{ state, dispatch }}>
      {children}
    </GameContext.Provider>
  );
};

export const useGameState = () => {
  const context = useContext(GameContext);
  if (!context) {
    throw new Error('useGameState must be used within a GameProvider');
  }
  return context;
};
