import React from 'react';
import { useGameState } from '../../hooks/useGameState';
import ProgressBar from '../ui/ProgressBar';

const Header = () => {
  const { currentLevel, totalLevels, score } = useGameState();

  return (
    <div className="text-center mb-8">
      <h1 className="text-4xl font-bold text-purple-800 mb-2">Enigmad</h1>
      <p className="text-gray-600 mb-4">
        Level {currentLevel + 1} of {totalLevels}
      </p>

      <div className="max-w-md mx-auto">
        <ProgressBar
          progress={currentLevel + 1}
          total={totalLevels}
          label="Progress"
          color="purple"
        />
      </div>

      <div className="mt-4 text-lg font-semibold text-purple-700">
        Score: {score}
      </div>
    </div>
  );
};

export default Header;
